// Simulate smoother drifts using sine waves
var service = "{#SERVICE}";
var now = new Date().getTime() / 1000;

function hash(str) {
    var h = 0;
    for (var i = 0; i < str.length; i++) {
        h = ((h << 5) - h) + str.charCodeAt(i);
        h |= 0;
    }
    return Math.abs(h);
}

var seed = hash(service);

// Main wave period varies per service (3-7 min)
var period = 180 + (seed % 240); 
var t = (now + seed) / period;

// Second harmonic (faster)
var t2 = (now + seed) / (period / 3.4);

// Mix waves: Main (0.7) + Ripple (0.3)
var signal = (Math.sin(t) * 0.7) + (Math.sin(t2) * 0.3);

// Normalize (-1 to 1) -> (0 to 1) 
var norm = (signal + 1) / 2;

// Bias: Power curve makes "healthy" (low) state more common
// ^3 keeps it low most of time, peaks occasionally
var val = Math.pow(norm, 3) * 100;

// Clamp 0-100
if (val < 0) val = 0;
if (val > 100) val = 100;

return Math.floor(val);
